package gfg.problems.easy;

import java.util.HashMap;

public class IsoMorphicString {
    public static void main(String[] args) {
//        String str1 = "aab";
//        String str2 = "xxy";
//        String str1 = "aabbc";
//        String str2 = "xyxyz";
        String str1 = "";
        String str2 = "";
        System.out.println(areIsomorphic(str1, str2));
    }

    public static boolean areIsomorphic(String str1, String str2) {
        if (str1.length() != str2.length())
            return false;
        HashMap<Character, Character> map1 = new HashMap<>();
        HashMap<Character, Character> map2 = new HashMap<>();
        for (int i = 0; i < str1.length(); i++) {
            if (map1.containsKey(str1.charAt(i))) {
                char ch = map1.get(str1.charAt(i));
                if (ch != str2.charAt(i))
                    return false;
            } else {
                map1.put(str1.charAt(i), str2.charAt(i));
            }
            if (map2.containsKey(str2.charAt(i))) {
                char ch = map2.get(str2.charAt(i));
                if (ch != str1.charAt(i))
                    return false;
            } else {
                map2.put(str2.charAt(i), str1.charAt(i));
            }
        }
        return true;
    }
}
